var Koa = require('koa')
var wechat = require('./wechat/g')  //可以看作是程序的第二个流程，实现验证 以及调用回复功能
var config = require('./config') //公众号的配置配置信息
var reply = require('./wx/reply')  //处理客户发过来的消息，回复逻辑
var Wechat = require('./wechat/wechat') //回复各种消息类型获取的api，封装在Wechat对象中

