'use strict'

var Promise = require('bluebird')
var request = require('request')
var util = require('./util')
var fs = require('fs')
var _ = require('lodash')

var prefix = 'https://api.weixin.qq.com/cgi-bin/'
var api = {
    accessToken : prefix + 'token?grant_type=client_credential',
    temporary: {
        upload : prefix + 'media/upload?',
        fetch: prefix + 'media/get?'
    },
    permanent: {
        upload: prefix + 'material/add_material?',
        fetch: prefix + 'material/get_material?',
        uploadNews: prefix + 'material/add_news?',
        uploadNewsPic:prefix + 'material/uploadimg?',
        del: prefix + 'material/del_material?',
        update: prefix + 'material/update_news?',
        count: prefix + 'material/get_materialcount?',
        batch: prefix + 'material/batchget_material?'
    },
    group: {
        create: prefix + 'groups/create?',
        fetch: prefix + 'groups/get?',
        check: prefix + 'groups/getid?',
        update:prefix + 'groups/update?',
        move: prefix + 'groups/members/update?',
        batchupdata: prefix + 'groups/members/batchupdate?',
        del: prefix + 'groups/delete?'
    },
    user: {
        remark: prefix +　'user/info/updateremark?',
        fetch: prefix +　'user/info?',
        batchFetch: prefix + 'user/info/batchget?',
        list: prefix +　'user/get?'
    },
    mass: {
        group: prefix + 'message/mass/sendall?',
        openId: prefix + 'message/mass/send?',
        del: prefix + 'message/mass/delete?',
        preview: prefix + 'message/mass/preview?',
        check: prefix + 'message/mass/get?'
    },
    menu: {
        create: prefix + 'menu/create?',
        get:　prefix +　'menu/get?',
        del: prefix + 'menu/delete?',
        current: prefix + 'get_current_selfmenu_info?'
    },   
    semantic: 'https://api.weixin.qq.com/semantic/semproxy/search?',
    ticket: {
        get : prefix +　'ticket/getticket?'
    }

}

function Wechat(opts){
    var that = this

    this.appID = opts.appID
    this.appSecret = opts.appSecret
    this.getAccessToken = opts.getAccessToken
    this.saveAccessToken = opts.saveAccessToken
    this.getTicket = opts.getTicket
    this.saveTicket = opts.saveTicket

    this.fetchAccessToken()
}

//获取ticket
Wechat.prototype.fetchTicket = function(access_token){
    var that = this

    return this.getTicket()
        .then(function(data){
            try{
                data = JSON.parse(data)
            }
            catch(e){
                return that.updateTicket(access_token)
            }

            if(that.isValidTicket(data)){
                return Promise.resolve(data)
            }
            else{
                return that.updateTicket(access_token)
            }
        })
        .then(function(data){
            that.saveTicket(data)

            return Promise.resolve(data)
        })
}

Wechat.prototype.updateTicket = function(access_token){

   var url = api.ticket.get + '&access_token=' + access_token + '&type=jsapi'

   return new Promise(function(resolve, reject){
       request({url: url, json: true}, function (error, response, body) {
            if (!error && response.statusCode === 200) {
                var data = body;
                var now = (new Date().getTime());
                var expires_in = now + (data.expires_in - 20) * 1000;
                data.expires_in = expires_in;
                resolve(data);
                //console.log(data);
            } else {
                reject('can not get ticket')
            }
        });     
   })

}

Wechat.prototype.isValidTicket = function(data){
    if(!data || !data.ticket || !data.expires_in){
        return false
    }

    var ticket = data.ticket
    var expires_in = data.expires_in
    var now = (new Date().getTime())

    if(ticket && now < expires_in){
        return true
    }
    else{
        return false
    }
}

//获取access_token
Wechat.prototype.fetchAccessToken = function(data){
    var that = this

    if(this.access_token && this.expires_in){
        if(this.isValidAccessToken(this)){
            return Promise.resolve(this)
        }
    }

    return this.getAccessToken()
        .then(function(data){
            try{
                data = JSON.parse(data)
            }
            catch(e){
                return that.updateAccessToken()
            }

            if(that.isValidAccessToken(data)){
                return Promise.resolve(data)
            }
            else{
                return that.updateAccessToken()
            }
        })
        .then(function(data){
            that.access_token = data.access_token
            that.expires_in = data.expires_in

            that.saveAccessToken(data)

            return Promise.resolve(data)
        })
}

Wechat.prototype.isValidAccessToken = function(data){
    if(!data || !data.access_token || !data.expires_in){
        return false
    }

    var access_token = data.access_token
    var expires_in = data.expires_in
    var now = (new Date().getTime())

    if(now < expires_in){
        return true
    }
    else{
        return false
    }
}

Wechat.prototype.updateAccessToken = function(){
   var appID = this.appID
   var appSecret = this.appSecret
   var url = api.accessToken + '&appid=' + appID + '&secret=' + appSecret

   return new Promise(function(resolve, reject){
       request({url: url, json: true}, function (error, response, body) {
            if (!error && response.statusCode === 200) {
                var data = body;
                var now = (new Date().getTime());
                var expires_in = now + (data.expires_in - 20) * 1000;
                data.expires_in = expires_in;
                resolve(data);
                //console.log(data);
            } else {
                reject('can not get access_token')
            }
        });     
   })

}

Wechat.prototype.uploadMaterial = function(type, material, permanent){
    var that = this
    var form = {}
    var uploadUrl = api.temporary.upload

    if(permanent){
        uploadUrl = api.permanent.upload

        _.extend(form, permanent)
    }

    if(type === 'pic'){
        uploadUrl = api.permanent.uploadNewsPic
    }

    if(type === 'news'){
        uploadUrl = api.permanent.uploadNews
        form = material
    }
    else{
        form.media = fs.createReadStream(material)
    }


   return new Promise(function(resolve, reject){
       that
           .fetchAccessToken()
           .then(function(data){
                var url = uploadUrl + 'access_token=' + data.access_token 

                if(!permanent) {
                    url += '&type=' + type
                }
                else{
                    form.access_token = data.access_token
                }

                var options = {
                    method: 'POST',
                    url: url,
                    json: true
                }

                if(type === 'news'){
                    options.body = form
                }
                else{
                    options.formData = form
                }

                request(options, function (error, response, body) {
                    if (!error && response.statusCode === 200) {
                        var _data = body;

                        if(_data){
                            resolve(_data);
                        }                     
                        //console.log(_data);
                    } else {
                        throw new Error('upload material fails')
                    }
                }) 

           })


