        let l = a.length > b.length ? b.length : a.length

        for(let i=0; i<l; i++) {
            if(a.charCodeAt(i) === b.charCodeAt(i)) continue

            return a.charCodeAt(i) > b.charCodeAt(i)
        }

        return a.length > b.length ? true : false
    }
}
